class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({type:"POST",url:$(e).attr("href")}).done((function(t){let l=parseInt($(e).attr("data-likes"));1==t.data.deleted?l-=1:l++;let i=$(e).attr("type");if($(e).attr("data-likes",l),"Post"===i){let t=$(e).attr("postId");$(`#post-${t}-likes`).html(`Likes: ${l}`)}else{let t=$(e).attr("commentId");$(`#comment-${t}-likes`).html(`Likes: ${l}`)}})).fail((function(t){console.log("error in completing the request",t)}))}))}}